import { useState, useEffect, useMemo } from "react";
import autologo from "./assets/myauto.png";
import styled from "styled-components";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Manufacturer } from "./domain/manufacturer";
import { Cat } from "./domain/cat";
import { Product } from "./domain/product";
import { Model } from "./domain/model";
import carphoto from "./assets/carphoto";
import truckphoto from "./assets/truckphoto";
import motophoto from "./assets/motophoto";
import Select from "react-select";
import {
  Button,
  ButtonGroup,
  Dropdown,
  InputGroup,
  FormControl,
} from "react-bootstrap";

const api1 = "https://static.my.ge/myauto/js/mans.json";
const api2 = "https://api2.myauto.ge/ka/getManModels?man_id=10";
const api3 = "https://api2.myauto.ge/ka/cats/get";
const api4 = "https://api2.myauto.ge/ka/products/";

function App() {
  // const [activeButton, setActiveButton] = useState<number | null>(null);

  // const handleMouseEnter = (index: number) => {
  //   setActiveButton(index);
  // };

  // const handleMouseLeave = () => {
  //   setActiveButton(null);
  // };

  // const buttonLabels = ["Cars", "Trucks", "Motorcycles"];
  // const ToggleWrapper = styled.div`
  //   display: flex;
  //   justify-content: space-between;
  //   width: 100px;
  //   background: lightgray;
  //   border-radius: 20px;
  //   padding: 5px;
  //   cursor: pointer;
  // `;

  // const [currency, setCurrency] = useState<string>("$");
  // const handleCurrencyChange = () => {
  //   setCurrency(currency === "$" ? "€" : "$");
  // };
  // const ToggleCircle = styled.div<{ active: boolean }>`
  //   width: 30px;
  //   height: 30px;
  //   border-radius: 50%;
  //   background: black;

  //   transition: transform 0.3s ease-in-out;
  //   transform: translateX(${(props) => (props.active ? "60px" : "0")});
  // `;
  <Select
    isMulti
    name="manufacturer"
    options={manufacturerOptions}
    className="basic-multi-select"
    classNamePrefix="select"
    onChange={handleManufacturerChange}
  />;
  const [currency, setCurrency] = useState("Dollars");
  const handleRentOptionChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    setSelectedRentOption(event.target.value === "იყიდება" ? false : true);
  };
  const toggleCurrency = () => {
    setCurrency((prevCurrency) => (prevCurrency === "gel" ? "usd" : "gel"));
  };

  const [selectedicon, setselectedicon] = useState<number>(0);

  const [selectedManufacturer, setSelectedManufacturer] =
    useState<Manufacturer | null>(null);
  const [selectedCat, setSelectedCat] = useState<Cat | null>(null);

  const [manufacturers, setmanufacturers] = useState<Manufacturer[]>([]);
  const [models, setmodels] = useState<Model[]>([]);
  const [cat_lst, setcat_lst] = useState<Cat[]>([]);
  const [product_lst, setproduct_lst] = useState<Product[]>([]);
  const [loading, setloading] = useState(true);
  const [selectedRentOption, setSelectedRentOption] = useState<boolean>(false);
  const [minPrice, setMinPrice] = useState<number | null>(null);
  const [maxPrice, setMaxPrice] = useState<number | null>(null);
  const [filteredCount, setFilteredCount] = useState<number>(0);
  // const CurrencyToggle: React.FC = () => {
  //   const [currency, setCurrency] = useState<string>("$");
  // };
  // This is a new function that will handle the change event from the select input
  const handleSearchButtonClick = () => {
    setFilteredCount(filteredProducts.length);
    // Do other stuff you need to do when search button is clicked...
  };

  const handleManufacturerChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    console.log("Selected Value: ", event.target.value); // Logs the value selected in the dropdown

    // Find the manufacturer that matches the selected value
    const selectedMan = manufacturers.find(
      (man) => man.man_name === event.target.value
    );
    console.log("Selected Manufacturer: ", selectedMan); // Logs the selected manufacturer
    setSelectedManufacturer(selectedMan || null);
  };

  const handleCatChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
