  //   const foundtuple = loaded_pages.find((tup) => tup[1] === page_num);
  //   if (foundtuple === undefined) {
  //     fetchProductList(api4 + "?Page=" + String(page_num));
  //   } else {
  //     setproduct_lst(foundtuple[0]);
