  const filteredAndSortedProducts = useMemo(() => {
    const filteredProducts = product_lst.filter((product) => {
      let matches = true;

      if (selectedManufacturers.length > 0) {
        matches = selectedManufacturers.some(
          (man) => String(man.value) === String(product.man_id)
        );
      }

      if (selectedCategories.length > 0) {
        matches =
          matches &&
          selectedCategories.some((cat) => cat.value === product.category_id);
      }
      if (selectedicon !== null) {
        matches = matches && product.vehicle_type === selectedicon;
      }
      if (selectedRentOption !== null) {
        matches = matches && product.for_rent === selectedRentOption;
      }
      if (minPrice !== null) {
        matches = matches && product.pred_min_price >= minPrice;
      }
      if (maxPrice !== null) {
        matches = matches && product.pred_max_price <= maxPrice;
      }
      return matches;
    });

    let sortedProducts = [...filteredProducts];
    if (sortOption !== null) {
      switch (sortOption) {
        case "Lowest Price":
          sortedProducts.sort((a, b) => a.price - b.price);
          break;
        case "Highest Price":
          sortedProducts.sort((a, b) => b.price - a.price);
          break;
        case "Alphabetical":
          sortedProducts.sort((a, b) => a.name.localeCompare(b.name));
          break;
        default:
          break;
      }
    }

    return sortedProducts;
  }, [
    product_lst,
    selectedCategories,
    selectedManufacturers,
    selectedicon,
    selectedRentOption,
    minPrice,
    maxPrice,
    sortOption, // Added sortOption to dependencies
  ]);
  const paginatedProducts = useMemo(() => {
    const startIndex = (page - 1) * 15; // Start index for the current page
    const endIndex = startIndex + 15; // End index for the current page
    return filteredAndSortedProducts.slice(startIndex, endIndex); // Return the products for the current page
  }, [filteredAndSortedProducts, page]);

  const change_onpage = (to_thispage: number) => {
    setpage(to_thispage);
  };

  // -----------------------------------------------------------------------------------------

  // Filtraion

  const handleRentOptionChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    setSelectedRentOption(event.target.value === "იყიდება" ? false : true);
  };
  const toggleCurrency = () => {
    setCurrency((prevCurrency) => (prevCurrency === "gel" ? "usd" : "gel"));
  };
  const handleManufacturersChange = (selectedList: OptionType[]) => {
    setSelectedManufacturers(selectedList);
  };

  const handleCategoriesChange = (selectedList: OptionType[]) => {
    setSelectedCategories(selectedList);
  };

  const handleSearchButtonClick = () => {
    let filteredProducts = product_lst.filter((product) => {
      let matches = true;

      if (selectedManufacturers.length > 0) {
        matches = selectedManufacturers.some(
          (man) => String(man.value) === String(product.man_id)
        );
      }

      if (selectedCategories.length > 0) {
        matches =
          matches &&
          selectedCategories.some((cat) => cat.value === product.category_id);
      }
      if (selectedicon !== null) {
        matches = matches && product.vehicle_type === selectedicon;
      }
      if (selectedRentOption !== null) {
        matches = matches && product.for_rent === selectedRentOption;
      }
      if (minPrice !== null) {
        matches = matches && product.pred_min_price >= minPrice;
      }
      if (maxPrice !== null) {
        matches = matches && product.pred_max_price <= maxPrice;
      }
      return matches;
    });
    setDisplayedProducts(filteredProducts);
    setFilteredCount(filteredProducts.length);
  };

  const handleManufacturerChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    const selectedMan = manufacturers.find(
      (man) => man.man_name === event.target.value
    );

    setSelectedManufacturer(selectedMan || null);
  };

  const handleCatChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedCat = cat_lst.find(
      (cat) => cat.seo_title === event.target.value
    );
    setSelectedCat(selectedCat || null);
  };
  const handleMinPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMinPrice(event.target.value ? Number(event.target.value) : null);
  };

  const handleMaxPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMaxPrice(event.target.value ? Number(event.target.value) : null);
  };
  // const filteredProducts = useMemo(() => {
