import { useState, useEffect, useMemo } from "react";
import autologo from "./assets/myauto.png";
import "./App.css";
import { Manufacturer } from "./domain/manufacturer";
import { Cat } from "./domain/cat";
import { Product } from "./domain/product";
import { Model } from "./domain/model";
import Vehicle_container from "./vehicle_container";
import { Meta } from "./domain/meta";
import Sidebar from "./sidebar";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import carphoto from "./assets/carphoto";
import truckphoto from "./assets/truckphoto";
import motophoto from "./assets/motophoto";
import { MultiSelect } from "react-multi-select-component";
import { InputGroup, FormControl } from "react-bootstrap";
import { OptionType } from "./interfaces";
import {
  catsApiBaseUrl,
  mansApiBaseUrl,
  modelsApiBaseUrl,
  productsApiBaseUrl,
} from "./api";

function App() {
  // GENERATION
  const [manufacturers, setmanufacturers] = useState<Manufacturer[]>([]);
  const [models, setmodels] = useState<Model[]>([]);
  const [cat_lst, setcat_lst] = useState<Cat[]>([]);
  const [product_lst, setproduct_lst] = useState<Product[]>([]);
  const [loading, setloading] = useState(true);
  const [meta, setmeta] = useState<Meta | undefined>();
  const [page, setPage] = useState(1);
  const [loaded_pages, set_loaded_pages] = useState<[Product[], number][]>([]);
  // GENERATION

  // filtration
  const [currency, setCurrency] = useState("Dollars");
  const [selectedicon, setselectedicon] = useState<number>(0);
  const [selectedManufacturer, setSelectedManufacturer] =
    useState<Manufacturer | null>(null);
  const [selectedCat, setSelectedCat] = useState<Cat | null>(null);
  const [selectedRentOption, setSelectedRentOption] = useState<boolean>(false);
  const [minPrice, setMinPrice] = useState<number | null>(null);
  const [maxPrice, setMaxPrice] = useState<number | null>(null);
  const [filteredCount, setFilteredCount] = useState<number>(0);
  const [selectedManufacturers, setSelectedManufacturers] = useState<
    OptionType[]
  >([]);
  const [selectedCategories, setSelectedCategories] = useState<OptionType[]>(
    []
  );
  const [displayedProducts, setDisplayedProducts] = useState<Product[]>([]);

  console.log("onpage: ", onpage);
  useEffect(() => {
    setnewapi_and_fetch(onpage);
  }, [onpage]);

  // filtrasion
  const fetchManufacturers = async () => {
    try {
      const response = await fetch(mansApiBaseUrl);
      const data = await response.json();
      setmanufacturers(data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchModels = async () => {
    try {
      const response = await fetch(modelsApiBaseUrl);
      const data = await response.json();

      setmodels(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchCatList = async () => {
    try {
      const response = await fetch(catsApiBaseUrl);
      const data = await response.json();

      setcat_lst(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchProductList = async (api: string) => {
    try {
      const response = await fetch(api);
      const data = await response.json();
      setmeta(data.data.meta);
      setproduct_lst(data.data.items);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchManufacturers();
    fetchModels();
    fetchCatList();
    fetchProductList(productsApiBaseUrl);
  }, []);

  useEffect(() => {
    setloading(false);
  }, [manufacturers, models, cat_lst, product_lst]);

  // GENERATING PRODUCTS, 15-PER PAGE

  const change_onpage = (to_thispage: number) => {
    setpage(to_thispage);
  };

  const addpagetolist = (page_prod: Product[], page_num: number) => {
    if (!loaded_pages.some((tup) => tup[1] === page_num)) {
      set_loaded_pages((prevPages) => [...prevPages, [page_prod, page_num]]);
    }
  };

  // -----------------------------------------------------------------------------------------

  // Filtraion

  const handleRentOptionChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    setSelectedRentOption(event.target.value === "იყიდება" ? false : true);
  };
  const toggleCurrency = () => {
    setCurrency((prevCurrency) => (prevCurrency === "gel" ? "usd" : "gel"));
  };
  const handleManufacturersChange = (selectedList: OptionType[]) => {
    setSelectedManufacturers(selectedList);
  };

  const handleCategoriesChange = (selectedList: OptionType[]) => {
    setSelectedCategories(selectedList);
  };

  const handleSearchButtonClick = () => {
    setpage(1); // Reset the page
    const filteredApi = generateFilteredApi(1); // page override
    console.log(filteredApi);
    fetchProductList(filteredApi);
  };

  const handleManufacturerChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    const selectedMan = manufacturers.find(
      (man) => man.man_name === event.target.value
    );

    setSelectedManufacturer(selectedMan || null);
  };

  const handleCatChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedCat = cat_lst.find(
      (cat) => cat.seo_title === event.target.value
    );
    setSelectedCat(selectedCat || null);
  };
  const handleMinPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMinPrice(event.target.value ? Number(event.target.value) : null);
  };

  const handleMaxPriceChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setMaxPrice(event.target.value ? Number(event.target.value) : null);
  };
  // const filteredProducts = useMemo(() => {
  //   return product_lst.filter((product) => {
  //     let matches = true;

  //     if (selectedManufacturers.length > 0) {
  //       matches = selectedManufacturers.some(
  //         (man) => String(man.value) === String(product.man_id)
  //       );
  //     }

  //     if (selectedCategories.length > 0) {
  //       matches =
  //         matches &&
  //         selectedCategories.some((cat) => cat.value === product.category_id);
  //     }
  //     if (selectedicon !== null) {
  //       matches = matches && product.vehicle_type === selectedicon;
  //     }
  //     if (selectedRentOption !== null) {
  //       matches = matches && product.for_rent === selectedRentOption;
  //     }
  //     if (minPrice !== null) {
  //       matches = matches && product.pred_min_price >= minPrice;
  //     }
  //     if (maxPrice !== null) {
  //       matches = matches && product.pred_max_price <= maxPrice;
  //     }
  //     return matches;
  //   });
  // }, [
  //   product_lst,
  //   selectedCategories,
  //   selectedManufacturers,
  //   selectedicon,
  //   selectedRentOption,
  //   minPrice,
  //   maxPrice,
  // ]);

  const filteredManufacturers = useMemo(() => {
    let filteredManufacturers: Manufacturer[] = [];

    if (selectedicon === 0) {
      filteredManufacturers = manufacturers.filter(
        (manufacturer) => manufacturer.is_car === "1"
      );
    } else if (selectedicon === 1) {
      filteredManufacturers = manufacturers.filter(
        (manufacturer) => manufacturer.is_spec === "1"
      );
    } else if (selectedicon === 2) {
      filteredManufacturers = manufacturers.filter(
        (manufacturer) => manufacturer.is_moto === "1"
      );
    } else {
      filteredManufacturers = manufacturers;
    }

    return filteredManufacturers;
  }, [selectedicon, manufacturers]);

  // -----------------------------------------------------------------------------------------

  // ------------------ GENERATING FILTERED_____________________________________________

  let ismobile = false;
  const useless = (
    <div className="container useless">
      მთავარი{" > "}ძიება{" > "} <span>იყიდება</span>
    </div>
  );

  return (
    <div>
      <header className="bg-light">
        <nav className="navbar navbar-expand-lg navbar-light">
          <div className="container">
            <a className="navbar-brand" href="#">
              <img src={autologo} alt="myauto" className="header-logo" />
            </a>
          </div>
        </nav>
      </header>

      <div>{ismobile ? null : useless}</div>

      <div>
        <div>
          <Sidebar
            selectedicon={selectedicon}
            setselectedicon={setselectedicon}
            selectedManufacturers={selectedManufacturers}
            setSelectedManufacturers={setSelectedManufacturers}
            manufacturers={manufacturers}
            carphoto={carphoto}
            truckphoto={truckphoto}
            motophoto={motophoto}
            handleRentOptionChange={handleRentOptionChange}
            filteredManufacturers={filteredManufacturers}
            handleManufacturersChange={handleManufacturersChange}
            cat_lst={cat_lst}
            selectedCategories={selectedCategories}
            handleCategoriesChange={handleCategoriesChange}
            currency={currency}
            toggleCurrency={toggleCurrency}
            handleMinPriceChange={handleMinPriceChange}
            handleMaxPriceChange={handleMaxPriceChange}
            handleSearchButtonClick={handleSearchButtonClick}
            filteredCount={filteredCount}
          />
        </div>

        <Vehicle_container
          addpagetolist={addpagetolist}
          change_onpage={change_onpage}
          product_lst={product_lst}
          models={models}
          cat_lst={cat_lst}
          manufacturers={manufacturers}
          loaded_pages={loaded_pages}
          on_page={onpage}
          meta={meta}
          setPage={setpage}
        />
      </div>
    </div>
  );
}

export default App;
