import { useState, useEffect, useMemo } from "react";
import autologo from "./assets/myauto.png";
import styled from "styled-components";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Manufacturer } from "./domain/manufacturer";
import { Cat } from "./domain/cat";
import { Product } from "./domain/product";
import { Model } from "./domain/model";
import carphoto from "./assets/carphoto";
import truckphoto from "./assets/truckphoto";
import motophoto from "./assets/motophoto";
import {
  Button,
  ButtonGroup,
  Dropdown,
  InputGroup,
  FormControl,
} from "react-bootstrap";

const api1 = "https://static.my.ge/myauto/js/mans.json";
const api2 = "https://api2.myauto.ge/ka/getManModels?man_id=10";
const api3 = "https://api2.myauto.ge/ka/cats/get";
const api4 = "https://api2.myauto.ge/ka/products/";

function App() {
  // const [activeButton, setActiveButton] = useState<number | null>(null);

  // const handleMouseEnter = (index: number) => {
  //   setActiveButton(index);
  // };

  // const handleMouseLeave = () => {
  //   setActiveButton(null);
  // };

  // const buttonLabels = ["Cars", "Trucks", "Motorcycles"];
  // const ToggleWrapper = styled.div`
  //   display: flex;
  //   justify-content: space-between;
  //   width: 100px;
  //   background: lightgray;
  //   border-radius: 20px;
  //   padding: 5px;
  //   cursor: pointer;
  // `;

  // const [currency, setCurrency] = useState<string>("$");
  // const handleCurrencyChange = () => {
  //   setCurrency(currency === "$" ? "€" : "$");
  // };
  // const ToggleCircle = styled.div<{ active: boolean }>`
  //   width: 30px;
  //   height: 30px;
  //   border-radius: 50%;
  //   background: black;

  //   transition: transform 0.3s ease-in-out;
  //   transform: translateX(${(props) => (props.active ? "60px" : "0")});
  // `;

  const [currency, setCurrency] = useState("Dollars");

  const toggleCurrency = () => {
    setCurrency((prevCurrency) => (prevCurrency === "gel" ? "usd" : "gel"));
  };
  const [selectedicon, setselectedicon] = useState<null | string>(null);
  const [selectedManufacturer, setSelectedManufacturer] =
    useState<Manufacturer | null>(null);

  const [manufacturers, setmanufacturers] = useState<Manufacturer[]>([]);
  const [models, setmodels] = useState<Model[]>([]);
  const [cat_lst, setcat_lst] = useState<Cat[]>([]);
  const [product_lst, setproduct_lst] = useState<Product[]>([]);
  const [loading, setloading] = useState(true);

  // const CurrencyToggle: React.FC = () => {
  //   const [currency, setCurrency] = useState<string>("$");
  // };
  // This is a new function that will handle the change event from the select input
  const handleManufacturerChange = (
    event: React.ChangeEvent<HTMLSelectElement>
  ) => {
    // Find the manufacturer that matches the selected value
    const selectedMan = manufacturers.find(
      (man) => man.man_name === event.target.value
    );
    setSelectedManufacturer(selectedMan || null);
  };
  const fetchManufacturers = async () => {
    try {
      const response = await fetch(api1);
      const data = await response.json();
      setmanufacturers(data);
      console.log(data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchModels = async () => {
    try {
      const response = await fetch(api2);
      const data = await response.json();

      setmodels(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchCatList = async () => {
    try {
      const response = await fetch(api3);
      const data = await response.json();

      setcat_lst(data.data);
    } catch (error) {
      console.error(error);
    }
  };

  const fetchProductList = async () => {
    try {
      const response = await fetch(api4);
      const data = await response.json();

      setproduct_lst(data.data.items);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchManufacturers();
    fetchModels();
    fetchCatList();
    fetchProductList();
    // const M = new Manufacturer(1314, "ye", 13, 14, 15);
    // M.barks();
  }, []);

  useEffect(() => {
    setloading(false);
  }, [manufacturers, models, cat_lst, product_lst]);

  const cat = <div>loaded</div>;

  type RadioType = {
    id: string;
    name: string;
    value: string;
  };

  const radios: RadioType[] = [
    { id: "radio1", name: "Radio 1", value: "radio1" },
    { id: "radio2", name: "Radio 2", value: "radio2" },
    { id: "radio3", name: "Radio 3", value: "radio3" },
  ];
  const filteredProducts = useMemo(() => {
    if (!selectedManufacturer) {
      return product_lst;
    }

    return product_lst.filter(
      (product) => product.man_id === selectedManufacturer.man_id
    );
  }, [product_lst, selectedManufacturer]);

  // console.log(product_lst[0]);
  // console.log(product_lst[0]?.bark());
  return (
    <div className="mainContainer">
      <header className="bg-light">
        <nav className="navbar navbar-expand-lg navbar-light">
          <div className="container">
            <a className="navbar-brand" href="#">
              <img src={autologo} alt="myauto" className="header-logo" />
            </a>
          </div>
        </nav>
      </header>
      {loading ? <div>loading</div> : cat}

      <div className="sidebar">
        <div className="button-container">
          <button
            className={`radioButton ${
              selectedicon === "car" ? "selected" : ""
            }`}
            onClick={() => setselectedicon("car")}
          >
            <div dangerouslySetInnerHTML={{ __html: carphoto }} />
          </button>
          <button
            className={`radioButton ${
              selectedicon === "truck" ? "selected" : ""
            }`}
            onClick={() => setselectedicon("truck")}
          >
            <div dangerouslySetInnerHTML={{ __html: truckphoto }} />
          </button>
          <button
            className={`radioButton ${
              selectedicon === "moto" ? "selected" : ""
            }`}
            onClick={() => setselectedicon("moto")}
          >
            <div dangerouslySetInnerHTML={{ __html: motophoto }} />
          </button>
        </div>
        <div className="sort_stats">
          <p className="sort_text">გარიგების ტიპი</p>
          <select className="dropdown sellType">
            <option>იყიდება</option>
            <option>ქირავდება</option>
          </select>
          <p className="sort_text">მწარმოებელი</p>
          <select
            className="dropdown manufacturerType"
            onChange={handleManufacturerChange}
          >
            {manufacturers.map((manufacturer) => (
              <option key={manufacturer.man_id} value={manufacturer.man_name}>
                {manufacturer.man_name}
              </option>
            ))}
          </select>
          <p className="sort_text">კატეგორია</p>
          <select className="dropdown categoryType">
            <option>Category</option>
            <option>Value 1</option>
            <option>Value 2</option>
            <option>Value 3</option>
          </select>
        </div>
        <div className="switch-container">
          <p>ფასი</p>
          <button
            className={`switch ${currency === "gel" ? "toggled" : ""}`}
            onClick={toggleCurrency}
          >
            <div className="toggleMoney"></div>
            <p className="curIcon">₾</p>

            <p className="curIcon">$</p>
          </button>
        </div>
        <div className="d-flex justify-content-between align-items-center mb-2">
          <InputGroup className="mb-3 d-flex align-items-center">
            <FormControl placeholder="დან" className="mr-2" />
            <span>-</span>
            <FormControl placeholder="მდე" className="ml-2" />
          </InputGroup>
          <div className="mb-3"></div>
        </div>
        <button className="searchBar">ძებნა 197,963</button>
      </div>
    </div>
  );
}

export default App;
